// Simply forwards to <sanitizer/asan_interface.h> when available from clang,
// or provides a simple stub implementation.
#pragma once

#if defined(__clang__)
#include <sanitizer/asan_interface.h>
#else  // !defined(__clang__)

#define ASAN_POISON_MEMORY_REGION(addr, size) (PW_UNUSED(addr), PW_UNUSED(size))
#define ASAN_UNPOISON_MEMORY_REGION(addr, size) \
  (PW_UNUSED(addr), PW_UNUSED(size))

#endif  // defined(__clang__)
